import React from 'react'

const LoadingSpinner = () =>{
    return (
        <img src='./images/loading_spinner.gif' className='img-spinner' />
    )
}


const MyButton = ({isLoading, onClick = () =>{}, title}) => {
  return (
    <button className='btn w-full rounded-md bg-cyan-500 p-2 text-white flex justify-center align-items-center' onClick={onClick}>
        {!isLoading ? <LoadingSpinner/> : title}
    </button>
  )
}

export default MyButton
