import React, { useState } from 'react'
import MyButton from '../components/MyButton'
import { logoutFunction } from '../config/functions'
import { useStore } from '../store'
import { updateLoginState } from '../store/actions'
import { ACCESS_TOKEN, LOCAL_LOGIN_STATE } from '../store/constants'

const Profile = () => {
  const [state, dispatch] = useStore()
  const { userLoginState } = state
  const [isLoading, setLoading] = useState(false)

  const userLogout =  () =>{
    setLoading(true)
    console.log("Logout")
    localStorage.removeItem(ACCESS_TOKEN, function(){
      let token = localStorage.getItem(ACCESS_TOKEN);
      if (token == null) {
        dispatch(updateLoginState(false))
      }
    })
    setLoading(false)
   }

  return (
    <div className='container px-10 m-auto align-items-center'>
      <MyButton onClick={userLogout} title={'Logout'} isLoading={isLoading}/>
    </div>
  )
}

export default Profile
