import React from 'react'
import { registerWithEmailAndPassword } from '../config/functions';
import { Login, Register } from './index'

function Authen() {
    const [uName, setUname] = React.useState('');
    const [uPw, setUpw] = React.useState('');
    const [authenMode, setAuthenMode] = React.useState(false);

    const LoginHandle = () => {
        console.log(uName + "/" + uPw)
        registerWithEmailAndPassword(uName, uPw)
        setUname('')
        setUpw('')
    }

    const SwitchAuthen = () => setAuthenMode(!authenMode)
    return (
        <div className='container-fluid'>
            <div className='authen-form mt-15 md:mt-20 form m-auto w-full md:w-1/5'>
            {authenMode ?
                <>
                    <Login />
                    <button className='btn w-full rounded-md bg-fuchsia-400 p-2 text-white' onClick={SwitchAuthen}>Join Now</button>
                </>

                :
                <>
                    <Register />
                    <button className='btn w-full rounded-md bg-fuchsia-400 p-2 text-white' onClick={SwitchAuthen}>Login</button>
                </>

            }
            </div>
        </div>
    )
}

export default Authen
