import React, { useEffect } from 'react'
import { Authen, Home } from './screens'
import { updateLoginState } from './store/actions'
import { useStore } from './store'
import { Outlet, Link } from "react-router-dom";
import { CheckUserLoggedIn, logoutFunction } from './config/functions'
import { LOCAL_LOGIN_STATE } from './store/constants';
const App = () => {
  const [state, dispatch] = useStore()
  const { userLoginState } = state

  useEffect(() => {
    DisplayComponent()
  }, [userLoginState])

  const userLogout =  () =>{
   if( logoutFunction){
    localStorage.setItem(LOCAL_LOGIN_STATE,false)
    dispatch(updateLoginState(false))
   }
  }

  const DisplayComponent = () =>{

    console.log("LOCAL STATE: "+localStorage.getItem(LOCAL_LOGIN_STATE))

    localStorage.getItem(LOCAL_LOGIN_STATE) ?
      (
        <div className='container-fluid h-10 bg-cyan-600'>
        <nav className='container '>
        <Link className='' to="/">Home</Link>
        <Link className='' to="/profile">Profile</Link>
        <button onClick={userLogout}>Logout</button>
        </nav>
        <Outlet />
      </div>
      )
    :
      (
        <Authen />
      )

  }

  return (
    <div>
      <DisplayComponent />
    </div>
  )
}

export default App
