import React, { useState } from 'react'
import MyButton from '../components/MyButton'
import { addNewDiary } from '../config/api_functions'
import { useStore } from '../store'
import {updateLoginState } from '../store/actions'
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import { useNavigate } from 'react-router-dom'

const AddDiary = () => {

    const navigate = useNavigate()

    const [state, dispatch] = useStore()
    const [content, setContent] = useState('')
    const {userLoginState} = state
    const handleNewDiary = async () =>{
        var diary = {
          "content":content,
          "display":true
        }
        await addNewDiary(diary)
        dispatch(updateLoginState(!userLoginState))
        navigate("/")

    }

  return (
   <div className='w-full p-2'>
        <h4 className='p-2'>Add new</h4>
        <ReactQuill 
        className='content-field w-full rounded-md bg-slate-200 text-black p-2'
        cols={5}
        rows={5}
        onChange={e => setContent(e.target.value)} 
        theme="snow"
        value={content} />
        <MyButton onClick={handleNewDiary} title={'Save'} icon={<i className="fa-solid fa-floppy-disk"></i>} />
   </div>
  )
}

export default AddDiary
