import React from 'react'
import { logInWithEmailAndPassword } from '../config/functions';
import { useStore } from '../store';
import { updateLoginState } from '../store/actions';
import { LOCAL_LOGIN_STATE } from '../store/constants';
const Login = () => {
    const [state, dispatch] = useStore()
    const {userLoginState} = state

    const [uName, setUname] = React.useState('');
    const [uPw, setUpw] = React.useState('');
    const [errorForm, setError] = React.useState(false)
    const [errorMsg, setErrorMsg] = React.useState('')
    const LoginHandle = async () => {
        var UserLogin = {
            "username":uName,
            "password":uPw
        }

       const loginResult = await logInWithEmailAndPassword(UserLogin);
       if(loginResult !== null){
            localStorage.setItem(LOCAL_LOGIN_STATE,true)
            dispatch(updateLoginState(true))
       }
    }

    const validateForm = () =>{
        if(uName.length < 3){
            setError(true)
            setErrorMsg('Username must be at least 3 characters')
        }

        if(uPw.length < 3){
            setError(true)
            setErrorMsg('Password must be at least 3 characters')
        }

        if(uName.length <3 && uPw.length < 3){
            setError(true)
            setErrorMsg('Username and password must be at least 3 characters')
        }

        if(!errorForm){
            LoginHandle()
        }
    }

    return (
        <div className='flex flex-col'>
            <h3 className='text-3xl font-bold'>Welcome back</h3>
            <input className='w-full rounded-md p-2 bg-slate-200' type={'text'} name='email' placeholder='Email' value={uName} onChange={(e) => setUname(e.target.value)} />
            <input className='w-full rounded-md p-2 bg-slate-200' type={'password'}  name='password' placeholder='Password' value={uPw} onChange={(e) => setUpw(e.target.value)} />
            <label className='w-full text-red-700 p-2 justify-content-center'>{errorMsg}</label>
            <button className='btn w-full rounded-md bg-cyan-500 p-2 text-white' onClick={validateForm}>Login</button>
        </div>


    )
}

export default Login
