import { async } from "@firebase/util";
import {
    getAuth,
    createUserWithEmailAndPassword,
    updateProfile,
    onAuthStateChanged,
    signInWithEmailAndPassword,
    signOut
} from "firebase/auth";
import { FIREBASE_UID } from "../store/constants";
import { app } from "./firebase";

const registerWithEmailAndPassword = async (User) => {
    var user = null;
    let fullname = User.fullname
    let username = User.username
    let password = User.password


    const auth = getAuth(app);
    await createUserWithEmailAndPassword(auth, username, password)
        .then((userCredential) => {
            // Signed in 
            user = userCredential.user;
            // ...
        })
        .catch((error) => {
            const errorMessage = error.message;
            console.log(errorMessage)
            // ...
        });

    return user;
}

const logInWithEmailAndPassword = async (User) => {
    var user = null;

    let username = User.username
    let password = User.password

    const auth = getAuth(app)
    await signInWithEmailAndPassword(auth, username, password)
    .then((userCredential)=>{
        user = userCredential.user;
    })
    .catch((error) => {
        const errorMessage = error.message;
        console.log(errorMessage)
        // ...
    });

    return user;
}

const CheckUserLoggedIn = () => {
    const auth = getAuth(app);
    onAuthStateChanged(auth, (user) => {
        if (user) {
          // User is signed in, see docs for a list of available properties
          // https://firebase.google.com/docs/reference/js/firebase.User
          const uid = user.uid;
          localStorage.setItem()
          // ...
        } else {
          // User is signed out
          // ...
        }
      });
}

const logoutFunction =  () =>{
    const auth = getAuth(app);
    signOut(auth)
    .then(()=>{
        console.log("Sign out success")
    })
    .catch((error)=>{
        console.log("Sign out failed", error)
    })
}

export { registerWithEmailAndPassword, logInWithEmailAndPassword, CheckUserLoggedIn, logoutFunction }
