import { UPDATE_LOGIN_STATE, UPDATE_SHOW_MENU_BAR } from "./constants";

export const updateLoginState = payload => ({
    type:UPDATE_LOGIN_STATE,
    payload
})

export const switchMenuBarShow = payload => ({
    type:UPDATE_SHOW_MENU_BAR,
    payload
})
