import React, { useState } from 'react'
import { Link, useNavigate, useParams } from 'react-router-dom'
import { getDiary } from '../config/api_functions'
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import { useStore } from '../store';
import { updateLoginState } from '../store/actions';
import MyButton from '../components/MyButton';
const DiaryDetails = () => {

    const {id}  = useParams()
    const navigate = useNavigate()
    const [state, dispatch] = useStore()
    const [content, setContent] = useState('')
    const {userLoginState} = state

    const [diary, setDiary] = useState({
      "content":content,
      "last_edited":'',
      "id":id
    })


    const handleEditDiary = async () =>{
        var diary = {
          "content":content,
          "display":true
        }

        dispatch(updateLoginState(!userLoginState))
        navigate("/")

    }

    const getDiaryDetails = async () =>{
        const result = await getDiary(id)
        setDiary(result)
    }

    getDiaryDetails()
  return (
    <div className='w-full p-2'>
        <h4 className='p-2'>Add new</h4>
        <ReactQuill className='content-field w-full rounded-md bg-slate-200 text-black p-2' theme="snow" value={diary.content} onChange={setContent} />
        <MyButton onClick={handleEditDiary} title={'Save'} icon={<i className="fa-solid fa-floppy-disk"></i>} />
        <Link to="/" ><MyButton title={'Back'} icon={<i className="fa-regular fa-hand-point-left"></i>}/></Link>
   </div>
  )
}

export default DiaryDetails
