import React from 'react'
import { logInWithEmailAndPassword } from '../config/functions';
import { useStore } from '../store';
const Login = () => {
    const [state, dispatch] = useStore()
    const {userLoginState} = state

    const [uName, setUname] = React.useState('');
    const [uPw, setUpw] = React.useState('');

    const LoginHandle = async () => {
        var UserLogin = {
            "username":uName,
            "password":uPw
        }

       const loginResult = logInWithEmailAndPassword(UserLogin);
       if(loginResult !== null){
            dispatch()
       }

    }

    return (
        <>
            <h3 className='text-3xl font-bold'>Welcome back</h3>
            <input className='w-full rounded-md p-2 bg-slate-200' name='email' placeholder='Email' value={uName} onChange={(e) => setUname(e.target.value)} />
            <input className='w-full rounded-md p-3 bg-slate-200' name='password' placeholder='Password' value={uPw} onChange={(e) => setUpw(e.target.value)} />
            <button className='btn w-full rounded-md bg-cyan-500 p-2 text-white' onClick={LoginHandle}>Login</button>
        </>


    )
}

export default Login
