import React, { useState } from 'react'
import { Link } from 'react-router-dom'
import MyButton from '../components/MyButton'
import { addNewDiary } from '../config/api_functions'
import { useStore } from '../store'
import { addMoreDiary, updateLoginState } from '../store/actions'

const AddDiary = () => {
    const [state, dispatch] = useStore()
    const [content, setContent] = useState('')
    const {userLoginState} = state
    const handleNewDiary = async () =>{
        var diary = {
          "content":content,
          "display":true
        }
        const result = await addNewDiary(diary)
        if(result === 'SUCCESS'){
          dispatch(addMoreDiary(diary))
        }

    }

  return (
   <div className='w-full p-2'>
        <h4 className='p-2'>Add new</h4>
        <textarea className='content-field w-full rounded-md bg-slate-200 text-black p-2' cols={5} rows={10} onChange={e => setContent(e.target.value)} ></textarea>

        <MyButton onClick={handleNewDiary} title={'Save'} icon={<i className="fa-solid fa-floppy-disk"></i>} />
   </div>
  )
}

export default AddDiary
