import { UPDATE_LOGIN_STATE } from "./constants";
const initState = {
    userLoginState: localStorage.getItem("LoginState") ? localStorage.getItem("LoginState") :false
}


function reducer(state, action){
    switch(action.type){
       case UPDATE_LOGIN_STATE:
        return {
            ...state,
            userLoginState: localStorage.setItem("LoginState",action.payload)
        }
        default:
            throw new Error('Sai action')
    }
}

export {initState};
export default reducer;
