import React, { useEffect } from 'react'
import { Authen, Home } from './screens'
import { updateLoginState, getDiariesList } from './store/actions'
import { useStore } from './store'
import { Outlet, Link } from "react-router-dom";
import { CheckLocalUID, CheckUserLoggedIn, getDiaries, logoutFunction } from './config/functions'
import { ACCESS_TOKEN, FIREBASE_UID, LOCAL_LOGIN_STATE } from './store/constants';
import Header from './components/Header';
import LeftMenuBar from './components/LeftMenuBar';
import { async } from '@firebase/util';
import { getAllDiary } from './config/api_functions';
const App = () => {
  const [state, dispatch] = useStore()
  const { userLoginState, diaries } = state

  useEffect(() => {
    DisplayComponent()
  }, [userLoginState])

  useEffect(() => {
    getData()
  }, [diaries])

  const getData = async () => {
    const list = await getAllDiary()
      dispatch(getDiariesList(list))
  }

  const DisplayComponent = () => {
    if (localStorage.getItem(ACCESS_TOKEN) != null) {
      return (
        <div className='container-fluid'>
          <Header />
          <div className='w-full md:w-1/3 m-auto'>
            <Outlet />
          </div>
        </div>
      )
    } else {
      return (
        <Authen />
      )
    }
  }

  return (
    <div>
      <LeftMenuBar />
      <DisplayComponent />
    </div>
  )
}

export default App
