import React from 'react'
import { useStore } from "../store";
import { registerWithEmailAndPassword } from '../config/functions';
import { updateLoginState } from '../store/actions';
import { LOCAL_LOGIN_STATE } from '../store/constants';
import MyButton from '../components/MyButton';

const Register = () => {
    const [state, dispatch] = useStore()
    const [isLoading, setLoading] = React.useState(false)

    const [uFName, setUfname] = React.useState('')
    const [uName, setUname] = React.useState('');
    const [uPw, setUpw] = React.useState('');
    const [errorMsg, setErrorMsg] = React.useState('')
    const RegisterHandle = async () => {
            setLoading(true)
            let UserRegister = {
                "fullname":uFName,
                "username":uName,
                "password":uPw
            }

            const registerResult = await registerWithEmailAndPassword(UserRegister);
            if(registerResult != null){
            localStorage.setItem(LOCAL_LOGIN_STATE,true)
            dispatch(updateLoginState(true))
            }else{
                console.log("Login failed")
            }

            setLoading(false)
    }

    const validateForm = () =>{
        var check = true
        if(uName.length < 3){
            check = false
            setErrorMsg('Username must be at least 3 characters')
        }

        if(uName.length < 3){
            check = false
            setErrorMsg('Username must be at least 3 characters')
        }

        if(uPw.length < 3){
            check = false
            setErrorMsg('Password must be at least 3 characters')
        }

        if(uName.length <3 && uPw.length < 3){
            check = false
            setErrorMsg('Username and password must be at least 3 characters')
        }

        if(check){
            setErrorMsg('')
        }
        return check
    }

    return (
        <>
            <h3 className='text-3xl font-bold'>Join now</h3>
            <div className='pt-5'>
                <input className='w-full rounded-md p-2 bg-slate-200' type={'text'}  name='fullname' placeholder='Full name' value={uFName} onChange={(e) => setUfname(e.target.value)} />
                <input className='w-full rounded-md p-2 bg-slate-200'  type={'email'} name='email' placeholder='Email' value={uName} onChange={(e) => setUname(e.target.value)} />
                <input className='w-full rounded-md p-2 bg-slate-200' type={'password'}  name='password' placeholder='Password' value={uPw} onChange={(e) => setUpw(e.target.value)} />
                {errorMsg === '' ? "" : <span className='p-2 text-red-500'>{errorMsg}</span>}
                <MyButton  onClick={RegisterHandle} title={'Go'} isLoading={isLoading} />
            </div>
        </>

    )
}

export default Register
