import { UPDATE_LOGIN_STATE } from "./constants";

const initState = {
    userLoginState: false,
}


function reducer(state, action){
    switch(action.type){
       case UPDATE_LOGIN_STATE:
        return {
            ...state,
            userLoginState: action.payload
        }
    }
}

export {initState};
export default reducer;
