import { async } from '@firebase/util';
import React from 'react'
import { useStore } from "../store";
import { UPDATE_LOGIN_STATE } from "../store/constants";
import { CheckUserLoggedIn, registerWithEmailAndPassword } from '../config/functions';

const Register = () => {
    const [state, dispatch] = useStore()
    const {userLoginState} = state

    const [uFName, setUfname] = React.useState('')
    const [uName, setUname] = React.useState('');
    const [uPw, setUpw] = React.useState('');

    const RegisterHandle = async () => {

            let UserRegister = {
                "fullname":uFName,
                "username":uName,
                "password":uPw
            }
            const loginResult = await registerWithEmailAndPassword(UserRegister);
            if(loginResult != null){
                dispatch(UPDATE_LOGIN_STATE)
            }
    }

    return (
        <>
            <h3 className='text-3xl font-bold'>Join now</h3>
            <div className='pt-5'>
                <input className='w-full rounded-md p-2 bg-slate-200' name='fullname' placeholder='Full name' value={uFName} onChange={(e) => setUfname(e.target.value)} />
                <input className='w-full rounded-md p-2 bg-slate-200' name='email' placeholder='Email' value={uName} onChange={(e) => setUname(e.target.value)} />
                <input className='w-full rounded-md p-3 bg-slate-200' name='password' placeholder='Password' value={uPw} onChange={(e) => setUpw(e.target.value)} />
                <button className='btn w-full rounded-md bg-cyan-500 p-2 text-white' onClick={RegisterHandle}>Go</button>
            </div>
        </>

    )
}

export default Register
