import React, { useEffect } from 'react'
import { Authen, Home } from './screens'
import { updateLoginState } from './store/actions'
import { useStore } from './store'
import { Outlet, Link } from "react-router-dom";
import { CheckUserLoggedIn, logoutFunction } from './config/functions'
const App = () => {
  const [state, dispatch] = useStore()
  const { userLoginState } = state

  useEffect(() => {
    console.log("Login State: ", userLoginState)
  }, [userLoginState])

  const userLogout =  () =>{
   if( logoutFunction){

    localStorage.setItem("LoginState",false)
    dispatch(updateLoginState(!userLoginState))
   }
  }

  const DisplayComponent = () =>{
    if(userLoginState || localStorage.getItem("LoginState")){

      return (
        <div className='container-fluid h-10 bg-cyan-600'>
        <nav className='container '>
        <Link className='' to="/">Home</Link>
        <Link className='' to="/profile">Profile</Link>
        <button onClick={userLogout}>Logout</button>
        </nav>
        <Outlet />
      </div>
      )
    }else{
      return (
        <Authen />
      )
    }
  }

  return (
    <div>
      <DisplayComponent />
    </div>
  )
}

export default App
