import { getDatabase, onValue, ref } from 'firebase/database';
import React from 'react'
import { app } from '../config/firebase';
import { useStore } from '../store'


import { getDiariesList } from '../store/actions';
import { FIREBASE_UID } from '../store/constants';
import MyButton from './MyButton';
const ListDiary = () => {
    const [state, dispatch] = useStore()
    const { diaries } = state;

    const getDiaryList = () => {
        const uid = localStorage.getItem(FIREBASE_UID)
        const db = getDatabase(app);
        const postListRef = ref(db, 'Diaries/' + uid+'/');
        onValue(postListRef, (snapshot) => {
            let listDiaries = []
            snapshot.forEach(childSnapshot => {
                let key = childSnapshot.key;
                let data = childSnapshot.val();
                listDiaries.push({ "key": key, "data": data })
            });
            dispatch(getDiariesList(listDiaries))
        })
    }

    const Diaries = () => {
        {
            diaries.map((diary, index) => {
                (<li className='w-full text-white' key={index}>{diary.content}</li>)
            })
        }
    }
    return (
        <div>
            <Diaries />
            <MyButton title={'Get'} onClick={getDiaryList} />
        </div>
    )
}

export default ListDiary
