import React, { useEffect } from 'react'
import { Authen, Home } from './screens'
import { updateLoginState } from './store/actions'
import { useStore } from './store'
import { Outlet, Link } from "react-router-dom";
import { CheckUserLoggedIn, logoutFunction } from './config/functions'
import { LOCAL_LOGIN_STATE } from './store/constants';
import Header from './components/Header';
import LeftMenuBar from './components/LeftMenuBar';
const App = () => {
  const [state, dispatch] = useStore()
  const { userLoginState,leftMenuBar } = state

  useEffect(() => {
    DisplayComponent()
  }, [userLoginState])

  const userLogout =  () =>{
   if(logoutFunction){
    localStorage.removeItem(LOCAL_LOGIN_STATE)
    dispatch(updateLoginState(false))
   }
  }

  const DisplayComponent = () =>{
    if(localStorage.getItem(LOCAL_LOGIN_STATE)){
      console.log("LOCAL STATE: "+localStorage.getItem(LOCAL_LOGIN_STATE))
      return (
        <div className='container-fluid'>
        <Header logout={userLogout} />
        <Outlet />
      </div>
      )
    }else{
      return (
        <Authen />
      )
    }
  }

  return (
    <div>
      {!leftMenuBar ? <LeftMenuBar /> : ''}
      <DisplayComponent />
    </div>
  )
}

export default App
