import React, { useState } from 'react'
import { Authen, Home } from './screens'
import {useGlobalUserState, setGlobalUserState} from './store/globalState'
function App() {
  const [userState] = useGlobalUserState("defaultUserLoginState")
  const changeStateLogin = () =>{
    setGlobalUserState("defaultUserLoginState",'true')
  }

  const checkState = () =>{
    if(userState === 'true'){
      return (
        <Home />
      )
    }else{
      <Authen />
    }
  }

  return (
    <div>
      {checkState()}
      <button onClick={changeStateLogin} className='btn border-l-indigo-300 w-full p-2'>Change State</button>
    </div>
  )
}

export default App
