import React from 'react'
import { useStore } from '../store'
import { switchMenuBarShow } from '../store/actions'

const LeftMenuBar = () => {
    const [state, dispatch] = useStore()
    const { leftMenuBar } = state

    const classMenu = () => {
        return leftMenuBar ?
            'show left-menu-bar'
            :
            'left-menu-bar'
    }

    return (
        <div className={classMenu()}>
            <button className='text-black' onClick={() => { dispatch(switchMenuBarShow(false)) }}><i className=" fa-light fa-square-chevron-left"></i></button>
        </div>
    )
}

export default LeftMenuBar
