import React, { useState } from 'react'
import { writeNewDiary } from '../config/functions'

const AddDiary = () => {

    const [content, setContent] = useState('')

    const handleNewDiary = () =>{
        if(content !== ''){
            writeNewDiary(content)
            setContent
        }
    }

  return (
   <div className='w-full p-2'>
        <h4 className='p-2'>Add new</h4>
        <textarea className='content-field w-full rounded-md bg-slate-400 text-white p-2' cols={5} rows={10} onChange={e => setContent(e.target.value)} ></textarea>
        <button onClick={handleNewDiary} className='w-full bg-green-600 p-2 rounded-md'>Save</button>
   </div>
  )
}

export default AddDiary
