import React, { useEffect } from 'react'
import { Authen, Home } from './screens'
import { updateLoginState, getDiariesList } from './store/actions'
import { useStore } from './store'
import { Outlet, Link } from "react-router-dom";
import { app } from './config/firebase';import {
  getDatabase,
  ref,
  onValue,
} from "firebase/database";
import { CheckLocalUID, CheckUserLoggedIn, logoutFunction } from './config/functions'
import { FIREBASE_UID, LOCAL_LOGIN_STATE } from './store/constants';
import Header from './components/Header';
import LeftMenuBar from './components/LeftMenuBar';
const App = () => {
  const [state, dispatch] = useStore()
  const { userLoginState, diaries } = state

  useEffect(() => {
    DisplayComponent()
  }, [userLoginState])

  useEffect(()=>{
    getDiaries()
  },[diaries])

  const userLogout =  () =>{
      localStorage.removeItem(LOCAL_LOGIN_STATE, ()=>{
      if(CheckLocalUID == null){
        dispatch(updateLoginState(false))
    }
    })
  }
  const getDiaries = () => {
    const uid = localStorage.getItem(FIREBASE_UID)
    const db = getDatabase(app);
    const postListRef = ref(db, 'Diaries/' + uid + '/');
    onValue(postListRef, (snapshot) => {
        let listDiaries = []
        snapshot.forEach(childSnapshot => {
            let key = childSnapshot.key;
            let data = childSnapshot.val();
            listDiaries.push({ "key": key, "data": data })
        });
        listDiaries.forEach((diary) => {
            dispatch(getDiariesList(diary))
        })
    })
}

  const DisplayComponent = () =>{
    if(localStorage.getItem(LOCAL_LOGIN_STATE)){
      return (
        <div className='container-fluid'>
        <Header logout={userLogout} />
        <div className='w-full md:w-1/3 m-auto'>
        <Outlet />
        </div>
      </div>
      )
    }else{
      return (
        <Authen />
      )
    }
  }

  return (
    <div>
      <LeftMenuBar />
      <DisplayComponent />
    </div>
  )
}

export default App
